<?php$db_host = "localhost";$db_username = "root";$db_password = '';$database = 'google_contact';try {    //connect with database server    $mysqli = new MySQLi($db_host,$db_username,$db_password);    // checking connection    if ($mysqli->connect_error) {        throw new Exception($mysqli->connect_error);    }    // if database is not exists, create new one    $sql = "CREATE DATABASE IF NOT EXISTS $database";    if (!$mysqli->query($sql)) {        throw new Exception($mysqli->error);    }    //connect/select database    if (!$mysqli->select_db($database)) {        throw new Exception($mysqli->error);    }} catch (Exception $e) {    echo "<h1 style='text-align: center; width: 100%; color: red'>Sorry system is under maintain.</h1> <pre>";    print_r($e);    exit();}// location for webserver$default_url = "http://{$_SERVER['HTTP_HOST']}/mms_it/php_2/google_contacts/";// sub-directory for all components$sub_url = array(    "main_url" => $default_url,    "css_url" => $default_url.'assets/css/',    "js_url" => $default_url.'assets/js/',    "icon_url" => $default_url.'assets/img/icons/',    "profile_img_url" => $default_url.'assets/img/profiles_img/',    "contact_img_url" => $default_url.'assets/img/contact_img/',    "contact_user_img_url" => $default_url.'assets/img/contact_user_img/',    "node_modules" => $default_url.'node_modules/',    "person" => $default_url.'person.php');date_default_timezone_set("Asia/Yangon");