<?phpglobal $sub_url;if (array_key_exists('toggle-star-btn',$_POST)) {    toggleStar($_POST['toggle-star-id']);}if (array_key_exists('delete-btn',$_POST)) {    deleteContact($_POST['delete-id']);}foreach (getContacts('star') as $contact) {    $id = $contact['id'];    $name = $contact['first_name'].' '.$contact['last_name'];    $star = $contact['star'];    $img = $contact['profile_img'];    $company = $contact['company'];    if ($company != '') {        $company = $company.', ';    }    $img_url = getImageUrl($img);    $person_url = $sub_url['person'].'?id='.$id;    $person_url_edit = $person_url.'&edit=1';?><div class="contact-div p-2 d-flex flex-row align-items-center">    <div class="h-100 d-flex flex-row align-items-center" style="width: 90%;" onclick="location.href = '<?php echo $person_url ?>'">        <div class="contact-name w-30">            <img class="me-2" src="<?php echo $img_url ?>" alt="google-profile">            <span><?php echo $name ?></span>        </div>        <div class="contact-email w-20">            <span><?php echo $contact['email'] ?></span>        </div>        <div class="contact-phone w-20">            <span><?php echo $contact['phone'] ?></span>        </div>        <div class="contact-job w-20">            <span><?php echo $company.' '.$contact['job_title'] ?></span>        </div>    </div>    <div class="w-10 ">        <form method="post" class="contact-list-setting justify-content-between align-items-center">            <span class="shadow-none" tabindex="0" data-bs-toggle="popover" data-bs-placement="bottom" data-bs-trigger="hover focus" data-bs-content="remove star">                <input type="hidden" name="toggle-star-id" value="<?php echo $id.'/'.$star ?>">                <button type="submit" name="toggle-star-btn"><i class="bi bi-star-fill h6 text-black"></i></button>            </span>            <span class="shadow-none" tabindex="0" data-bs-toggle="popover" data-bs-placement="bottom" data-bs-trigger="hover focus" data-bs-content="edit contact">                <a href="<?php echo $person_url_edit ?>"><i class="bi bi-pen h6 text-black"></i></a>            </span>            <span class="shadow-none" tabindex="0" data-bs-toggle="popover" data-bs-placement="bottom" data-bs-trigger="hover focus" data-bs-content="delete contact">                <button type="button" class="del-active"><i class="bi bi-trash h6 text-black"></i></button>            </span>        </form>    </div>    <form method="post" class="del-confirm-div h-100 bg-light d-flex flex-row justify-content-center">        <input type="hidden" name="delete-id" value="<?php echo $id ?>">        <div class="d-flex justify-content-center align-items-center w-50 h-100">            <p class="del-text fw-bold m-0">are you sure</p>        </div>        <button type="submit" name="delete-btn" class="w-25 del-yes fw-bold">YES</button>        <button type="button" class="w-25 del-no fw-bold del-cancel">NO</button>    </form></div><?php } ?>